import React, { useState,useEffect } from 'react'
import axios from 'axios'




const index = () => {

const [data, setData ] = useState([]);

useEffect(()=>{
  fetchData();
},[]);


const fetchData = async ()=>{

  try {
    const tasks = await axios.get('http://localhost:3000/tasks');
    const response = tasks.data;
    console.log(response);
    setData(response.tasks);
    
  } catch (error) {
    
    console.log(error);
  }
}

const handleDeleteTask = async (id) =>{
  try {
    const confirm = prompt("are you sure you want to delete ?");

    if(confirm){
      const deletedTask = await axios.delete(`http://localhost/3000/delete/${id}`);
      if(deletedTask){
        window.location.href = '/task';
      }
    }

  } catch (error) {
    console.log(error);
  }
}

const updateTask = async(id)=>{
  try {
    const tasks = await axios.get(`http://localhost:3000/tasks`);
    const result = tasks.data;
    console.log(result)
    setData(result.tasks)
  } catch (error) {
    console.log(error);
  }

  const handleSubmit = e =>{
    e.preventDefault();
  }
}







  return (
    <form onSubmit={handleSubmit} >
    <div className='bg-black h-full'>

        <input type='text' placeholder='what you wanna do ?' className='ml-10 mt-10 p-4 py-2 rounded-lg'/>
        <button  type='submit' className='text-black ml-4 border border-red-700 rounded-lg bg-red-700 p-2 py-px'>Add task</button>
     
     
    </div>
    </form>
  )
}

export default index
