import React, { useState } from 'react';
import TodoForm from './TodoForm';
import Todo from './Todo'; // Import Todo component
import { v4 as uuidv4 } from 'uuid';

const TodoWrapper = () => {
  const [todos, setTodos] = useState([]);

  const addTodo = todo => {
    setTodos([
      ...todos,
      {
        id: uuidv4(),
        task: todo,
        completed: false,
        isEditing: false
      }
    ]);
    console.log(todos)
  };

  return (
    <div className='TodoWrapper'>
        <h1>Get </h1>
      <TodoForm addTodo={addTodo} />
      {/* Map through todos and render Todo component for each todo */}
      {todos.map((todo) => (
        <Todo task={todo} key={todo.id} /> // Use todo.id as the key
      ))}
    </div>
  );
};

export default TodoWrapper;
