import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Index = () => {
  const [data, setData] = useState([]);
  const [value, setValue] = useState('');

  useEffect(() => {
    fetchData();
  }, []);

  

  const handleDeleteTask = async (id) => {
    try {
      const confirmDelete = window.confirm("Are you sure you want to delete?");
      if (confirmDelete) {
        await axios.delete(`http://localhost/3000/delete/${id}`);
        fetchData(); // Refresh tasks after deletion
      }
    } catch (error) {
      console.log(error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const newTask = { description: value };
      await axios.post('http://localhost:3000/tasks', newTask);
      setValue(''); // Clear input field after submission
      fetchData(); // Refresh tasks after adding a new one
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div className='bg-black h-full'>
        <input
          type='text'
          placeholder='What do you want to do?'
          className='ml-10 mt-10 p-4 py-2 rounded-lg'
          value={value}
          onChange={(e) => setValue(e.target.value)}
        />
        <button type='submit' className='text-black ml-4 border border-red-700 rounded-lg bg-red-700 p-2 py-px'>
          Add task
        </button>
      </div>
    </form>
  );
};

export default Index;
